import React, { useState } from "react";
import "./App.css";

const INITIAL_DELIVERY_DISTANCE = 1000;
const RUSH_HOUR_MULTIPLIER = 1.2;

type FormData = {
  cartValue: number;
  deliveryDistance: number;
  amountOfItems: number;
  orderTime: string;
};

function App() {
  const defaultFormData = {
    cartValue: 0,
    deliveryDistance: 0,
    amountOfItems: 0,
    orderTime: "",
  };

  const defaultDeliveryFee = {
    defaultDeliveryFee: 0,
  };

  const [formData, setFormData] = useState<FormData>(defaultFormData);
  const { cartValue, deliveryDistance, amountOfItems, orderTime } = formData;
  const [deliveryFee, setDeliveryFee] = useState(defaultDeliveryFee);

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.id]: e.target.value,
    }));
  };

  const onTimeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData((prevState) => ({
      ...prevState,
      ordertime: e.target.value,
    }));
  };

  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    const cartFee = cartFeeCalc(cartValue);
    const distFee = distFeeCalc(deliveryDistance);
    const itemFee = itemFeeCalc(amountOfItems);
    let totalFee = cartFee + distFee + itemFee;
    const orderDateTime = new Date(orderTime);
    const isRush =
      orderDateTime.getUTCDay() === 5 &&
      orderDateTime.getUTCHours() >= 15 &&
      orderDateTime.getUTCHours() < 19;

    if (isRush) {
      totalFee *= 1.2;
    }

    if (totalFee > 15) {
      totalFee = 15;
    }

    setDeliveryFee({ defaultDeliveryFee: totalFee });

    setFormData(defaultFormData);
  };

  // -- functions --//

  const validateForm = () => {};

  const cartFeeCalc = (cartvalue: number): number => {
    if (cartvalue < 10.0) {
      const smallOrderSurcharge = 10.0 - cartvalue;
      return smallOrderSurcharge;
    } else {
      return 0;
    }
  };

  const distFeeCalc = (deliverydistance: number): number => {
    const baseFee = 2.0;
    const additionalFeePer500m = 1.0;
    const minimumFee = 1.0;

    let fee = baseFee;

    if (deliverydistance > INITIAL_DELIVERY_DISTANCE) {
      const additionalDistance = deliverydistance - INITIAL_DELIVERY_DISTANCE;
      const additionalFee =
        Math.ceil(additionalDistance / 500) * additionalFeePer500m;
      fee += additionalFee;
    }

    fee = Math.max(fee, minimumFee);

    return fee;
  };

  const itemFeeCalc = (ammountofitems: number): number => {
    const bulkFee = 1.2;
    const feeFor5OrMore = 0.5;

    let fee = 0;
    if (ammountofitems > 12) {
      fee += bulkFee;
    }
    if (ammountofitems > 4) {
      fee += (ammountofitems - 4) * feeFor5OrMore;
    }

    return fee;
  };

  return (
    <div className="App">
      <h2 className="App-heading"> Delivery Fee Calculator</h2>
      <form className="Form" onSubmit={onSubmit}>
        <label htmlFor="cartvalue">
          Cart Value:
          <input
            type="Float"
            id="cartvalue"
            value={cartValue}
            onChange={onChange}
            data-test-id="cartValue"
          />
        </label>
        <label htmlFor="deliverydistance">
          Delivery distance:
          <input
            type="number"
            id="deliverydistance"
            value={deliveryDistance}
            onChange={onChange}
            data-test-id="deliveryDistance"
          />
        </label>
        <label htmlFor="ammountofitems">
          Amount of items:
          <input
            type="number"
            id="ammountofitems"
            value={ammountOfItems}
            onChange={onChange}
            data-test-id="ammountOfItems"
          />
        </label>
        <label htmlFor="ordertime">
          Order Time:
          <input
            type="datetime-local"
            id="ordertime"
            value={orderTime}
            onInput={onTimeChange}
            data-test-id="orderTime"
          />
        </label>
        <button className="form-button" type="submit">
          Calculate delivery price
        </button>
        <p>Delivery price : {deliveryFee.defaultDeliveryFee} €</p>
      </form>
    </div>
  );
}

export default App;
