import React, { useState } from "react";
import "./App.css";

function App() {
  const defaultFormData = {
    cartvalue: "",
    deliverydistance: "",
    ammountofitems: "",
    ordertime: "",
  };

  const defaultDeliveryFee = {
    defaultDeliveryFee: 0,
  };

  const [formData, setFormData] = useState(defaultFormData);
  const { cartvalue, deliverydistance, ammountofitems, ordertime } = formData;
  const [deliveryFee, setDeliveryFee] = useState(defaultDeliveryFee);

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.id]: e.target.value,
    }));
  };

  const onTimeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData((prevState) => ({
      ...prevState,
      ordertime: e.target.value,
    }));
  };

  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    const cartFee = cartFeeCalc(parseFloat(cartvalue));
    const distFee = distFeeCalc(parseInt(deliverydistance));
    const itemFee = itemFeeCalc(parseInt(ammountofitems));
    let totalFee = cartFee + distFee + itemFee;

    const orderTime = new Date(ordertime);
    const isRush =
      orderTime.getUTCDay() === 5 &&
      orderTime.getUTCHours() >= 15 &&
      orderTime.getUTCHours() < 19;

    if (isRush) {
      totalFee *= 1.2;
    }

    if (totalFee > 15) {
      totalFee = 15;
    }

    setDeliveryFee({ defaultDeliveryFee: totalFee });

    setFormData(defaultFormData);
  };

  // -- functions --//

  const cartFeeCalc = (cartvalue: number) => {
    if (cartvalue < 10.0) {
      const smallOrderSurcharge = 10.0 - cartvalue;
      return smallOrderSurcharge;
    } else {
      return 0;
    }
  };

  const distFeeCalc = (deliverydistance: number) => {
    const baseFee = 2.0;
    const additionalFeePer500m = 1.0;
    const minimumFee = 1.0;

    let fee = baseFee;

    if (deliverydistance > 1000) {
      const additionalDistance = deliverydistance - 1000;
      const additionalFee =
        Math.ceil(additionalDistance / 500) * additionalFeePer500m;
      fee += additionalFee;
    }

    fee = Math.max(fee, minimumFee);

    return fee;
  };

  const itemFeeCalc = (ammountofitems: number) => {
    const bulkFee = 1.2;
    const feeFor5OrMore = 0.5;

    let fee = 0;
    if (ammountofitems > 12) {
      fee += bulkFee;
    }
    if (ammountofitems > 4) {
      fee += (ammountofitems - 4) * feeFor5OrMore;
    }

    return fee;
  };

  return (
    <div className="App">
      <h2 className="App-heading"> Delivery Fee Calculator</h2>
      <form className="Form" onSubmit={onSubmit}>
        <label htmlFor="cartvalue">
          Cart Value:
          <input
            type="Float"
            id="cartvalue"
            value={cartvalue}
            onChange={onChange}
            data-test-id="cartValue"
          />
        </label>
        <label htmlFor="deliverydistance">
          Delivery distance:
          <input
            type="number"
            id="deliverydistance"
            value={deliverydistance}
            onChange={onChange}
            data-test-id="deliveryDistance"
          />
        </label>
        <label htmlFor="ammountofitems">
          Amount of items:
          <input
            type="number"
            id="ammountofitems"
            value={ammountofitems}
            onChange={onChange}
            data-test-id="ammountOfItems"
          />
        </label>
        <label htmlFor="ordertime">
          Order Time:
          <input
            type="datetime-local"
            id="ordertime"
            value={ordertime}
            onInput={onTimeChange}
            data-test-id="orderTime"
          />
        </label>
        <button className="form-button" type="submit">
          Calculate delivery price
        </button>
        <p>Delivery price : {deliveryFee.defaultDeliveryFee} €</p>
      </form>
    </div>
  );
}

export default App;
