import React, { useState } from "react";
import "./App.css";

function App() {
  const defaultFormData = {
    cartvalue: "",
    deliverydistance: "",
    ammountofitems: "",
  };

  const defaultDeliveryFee = {
    defalutDeliveryFee: 0,
  };

  const [formData, setFormData] = useState(defaultFormData);
  const { cartvalue, deliverydistance, ammountofitems } = formData;
  const [deliveryFee, setDeliveryFee] = useState(defaultDeliveryFee);

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.id]: e.target.value,
    }));
  };

  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    console.log(formData);
    console.log(deliveryFee);
    setFormData(defaultFormData);
    setDeliveryFee(defaultDeliveryFee);
  };

  /*functions*/

  return (
    <div className="App">
      <form className="Form" onSubmit={onSubmit}>
        <label htmlFor="cartvalue">
          Cart Value:
          <input
            type="Float"
            id="cartvalue"
            value={cartvalue}
            onChange={onChange}
          />
        </label>
        <label htmlFor="deliverydistance">
          Delivery distance:
          <input
            type="number"
            id="deliverydistance"
            value={deliverydistance}
            onChange={onChange}
          />
        </label>
        <label>
          Ammount of items:
          <input
            type="number"
            id="ammountofitems"
            value={ammountofitems}
            onChange={onChange}
          />
        </label>
        <button type="submit">Calculate delivery price</button>
        <p>Delivery price: {deliveryFee.defalutDeliveryFee}</p>
      </form>
    </div>
  );
}

export default App;
