import React, { useState } from "react";
import "./App.css";

function App() {
  const defaultFormData = {
    cartvalue: "",
    deliverydistance: "",
    ammountofitems: "",
  };

  const defaultDeliveryFee = {
    defalutDeliveryFee: 0,
  };

  const [formData, setFormData] = useState(defaultFormData);
  const { cartvalue, deliverydistance, ammountofitems } = formData;
  const [deliveryFee, setDeliveryFee] = useState(defaultDeliveryFee);

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.id]: e.target.value,
    }));
  };

  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    const cartFee = cartFeeCalc(parseFloat(cartvalue));
    const distFee = distFeeCalc(parseFloat(cartvalue));
    const itemFee = itemFeeCalc(parseFloat(cartvalue));

    const totalFee = cartFee + distFee + itemFee;

    setDeliveryFee({ defalutDeliveryFee: totalFee });
    setFormData(defaultFormData);
  };

  /*functions*/

  const cartFeeCalc = (cartvalue: number) => {
    if (cartvalue < 10.0) {
      const smallOrderSurcharge = 10.0 - cartvalue;
      return smallOrderSurcharge;
    } else {
      return 0;
    }
  };

  const distFeeCalc = (deliverydistance: number) => {
    const baseFee = 2.0;
    const additionalFeePer500m = 1.0;
    const minimumFee = 1.0;

    let fee = baseFee;

    if (deliverydistance > 1000) {
      const additionalDistance = deliverydistance - 1000;
      const additionalFee =
        Math.ceil(additionalDistance / 500) * additionalFeePer500m;
      fee += additionalFee;
    }

    fee = Math.max(fee, minimumFee);

    return fee;
  };

  const itemFeeCalc = (ammountofitems: number) => {
    const bulkFee = 1.2;
    const feeFor5OrMore = 0.5;
    const maxFee = 15.0;

    let baseFee = 0;
    if (ammountofitems > 12) {
      baseFee += bulkFee;
    }
    if (ammountofitems > 4) {
      baseFee += (ammountofitems - 4) * feeFor5OrMore;
    }
    if (baseFee > 15.0) {
      baseFee = maxFee;
    }

    return baseFee;
  };

  return (
    <div className="App">
      <form className="Form" onSubmit={onSubmit}>
        <label htmlFor="cartvalue">
          Cart Value:
          <input
            type="Float"
            id="cartvalue"
            value={cartvalue}
            onChange={onChange}
          />
        </label>
        <label htmlFor="deliverydistance">
          Delivery distance:
          <input
            type="number"
            id="deliverydistance"
            value={deliverydistance}
            onChange={onChange}
          />
        </label>
        <label>
          Ammount of items:
          <input
            type="number"
            id="ammountofitems"
            value={ammountofitems}
            onChange={onChange}
          />
        </label>
        <button type="submit">Calculate delivery price</button>
        <p>Delivery price : {deliveryFee.defalutDeliveryFee} â‚¬</p>
      </form>
    </div>
  );
}

export default App;
